Method.Implementation:
set newObj.Description = $REPLACE($REPLACE(newObj.Description, $c(10), ""), "&quot;", """") set newObj.City = $REPLACE(newObj.City, $C(10), "") set newObj.Club = $REPLACE(newObj.Club, $C(10), "")
// Remove " " from field set newObj.MaleLastName = $SELECT($L(newObj.MaleLastName) < 2 : "", $$$YES : newObj.MaleLastName) set newObj.FemaleLastName = $SELECT($L(newObj.FemaleLastName) < 2 : "", $$$YES : newObj.FemaleLastName)
if (newObj.MaleLastName = newObj.FemaleLastName) && (newObj.MaleFirstName = newObj.FemaleFirstName){ set newObj.FemaleLastName = "" set newObj.FemaleFirstName = "" set newObj.FemaleNumber = "" }
if (..IdInternalIdxExists(newObj.IdInternal) = $$$NO){ quit newObj.%Save() }
// If exists - update set oldObj = ..IdInternalIdxOpen(newObj.IdInternal) set oldObj.Amount = newObj.Amount set oldObj.City = newObj.City set oldObj.Club = newObj.Club set oldObj.CodeOne = newObj.CodeOne set oldObj.CodeTwo = newObj.CodeTwo //set oldObj.Created = newObj.Created set oldObj.GroupIdInternal = newObj.GroupIdInternal set oldObj.Description = newObj.Description
if ($LENGTH(newObj.FemaleLastName) < 2) set newObj.FemaleLastName = "" if ($LENGTH(newObj.FemaleFirstName) < 2) set newObj.FemaleFirstName = "" set oldObj.FemaleLastName = newObj.FemaleLastName set oldObj.FemaleFirstName = newObj.FemaleFirstName set oldObj.FemaleDOBOrAge = newObj.FemaleDOBOrAge set oldObj.FemaleNumber = +newObj.FemaleNumber
if ($LENGTH(newObj.MaleLastName) < 2) set newObj.MaleLastName = "" if ($LENGTH(newObj.MaleFirstName) < 2) set newObj.MaleFirstName = "" set oldObj.MaleLastName = newObj.MaleLastName set oldObj.MaleFirstName = newObj.MaleFirstName set oldObj.MaleDOBOrAge = newObj.MaleDOBOrAge set oldObj.MaleNumber = +newObj.MaleNumber
set oldObj.OrderIdInternal = newObj.OrderIdInternal set oldObj.TournamentIdExternal = newObj.TournamentIdExternal set oldObj.Trainers = newObj.Trainers set oldObj.TStatus = newObj.TStatus
quit oldObj.%Save()

