Method.Implementation:
set proxy = ##super(params)
set proxy.athlete = ..Athlete.ConvertToProxyObject()
set proxy.athlete.otherInfo.city = ..City
set proxy.athlete.otherInfo.club = ..Club
set proxy.athlete.otherInfo.mainTrainer = ..MainTrainer
set proxy.athlete.otherInfo.otherTrainers = ..OtherTrainers

set proxy.athlete.competitionsCount = "-"
set proxy.athlete.paymentsCount = "-"
if $ISOBJECT(proxy.competition.tournament){
	if (params.loadPrtObjCompetitionsCount){
		set proxy.athlete.competitionsCount = ..PrtObjCompetitionsCount
	}

	if (params.loadPrtObjPaymentsCount){
		set proxy.athlete.paymentsCount = ..PrtObjPaymentsCount
	}
}

quit proxy

if ..CmpAtWDSFIdxExists(competition.%Id(), athlete.id) { set obj = ..CmpAtWDSFIdxOpen(competition.%Id(), athlete.id) set obj.IsActive = 1 set st = obj.%Save() quit obj }
set obj = ..%New()
// Свойства класса set obj.Athlete = ##class(PersonWDSF).%OpenId(athlete.id) if ($LENGTH(athlete.dob = 10)) set obj.DOB = $ZDH(athlete.dob, 3)
// Свойства базового класса set obj.Recorder = recorder set obj.Competition = competition set obj.Tournament = competition.Tournament
set obj.FullName = ##class(Dance.Data.Localization.LText).Create("") set st = ..UpdateParticipantFullName(obj) quit:$$$ISERR(st) $$$NULLOREF
set obj.Country = obj.Athlete.Country set obj.City = athlete.otherInfo.city set obj.Club = athlete.otherInfo.club set obj.MainTrainer = athlete.otherInfo.mainTrainer set obj.OtherTrainers = athlete.otherInfo.otherTrainers
set obj.PrtObjKey = obj.Athlete.%ClassName(1)_"@"_obj.Athlete.%Id()
set st = obj.%Save()
quit obj
